function Agrotips_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_11': 'Agrotips_mobilebutton_11',
        'spacer_21': 'Agrotips_spacer_21',
        'mobileworkarea_18': 'Agrotips_mobileworkarea_18',
        'spacer_22': 'Agrotips_spacer_22',
        'html_23': 'Agrotips_html_23',
        'html_24': 'Agrotips_html_24',
        'mobileimage_3': 'Agrotips_mobileimage_3',
        'mobilelabel_20': 'Agrotips_mobilelabel_20',
        'Crops': 'Agrotips_Crops',
        'Insurance': 'Agrotips_Insurance',
        'Agrotips': 'Agrotips_Agrotips'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'Agrotips';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Agrotips_onLoad = function() {
            Agrotips_elementsExtraJS();

            Agrotips_deviceEvents();
            Agrotips_windowEvents();
            Agrotips_elementsEvents();
        };

    // screen window events


    function Agrotips_windowEvents() {

        $('#Agrotips').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function Agrotips_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Agrotips_elementsExtraJS() {
        // screen (Agrotips) extra code

    };

    // screen elements handler


    function Agrotips_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Agrotips_mobileheader [name="mobilebutton_11"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    $('[id="Agrotips_panel_2"]').panel("open");

                }
            },
        }, '#Agrotips_mobileheader [name="mobilebutton_11"]');

        $(document).off("click", '#Agrotips_panel_2 [name="Crops"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('crops', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        }, '#Agrotips_panel_2 [name="Crops"]');
        $(document).off("click", '#Agrotips_panel_2 [name="Insurance"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Insurance', {
                        transition: 'pop',
                        reverse: false
                    });

                }
            },
        }, '#Agrotips_panel_2 [name="Insurance"]');
        $(document).off("click", '#Agrotips_panel_2 [name="Agrotips"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Agrotips', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        }, '#Agrotips_panel_2 [name="Agrotips"]');

    };

    $(document).off("pagebeforeshow", "#Agrotips").on("pagebeforeshow", "#Agrotips", function(event, ui) {
        Apperyio.CurrentScreen = "Agrotips";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Agrotips_onLoad();
};

$(document).off("pagecreate", "#Agrotips").on("pagecreate", "#Agrotips", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Agrotips_js();
});