function Screen4_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_11': 'Screen4_mobilebutton_11',
        'spacer_21': 'Screen4_spacer_21',
        'mobileworkarea_18': 'Screen4_mobileworkarea_18',
        'mobileimage_3': 'Screen4_mobileimage_3',
        'mobilelabel_20': 'Screen4_mobilelabel_20',
        'Crops': 'Screen4_Crops',
        'Insurance': 'Screen4_Insurance',
        'Agrotips': 'Screen4_Agrotips'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'Screen4';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Screen4_onLoad = function() {
            Screen4_elementsExtraJS();

            Screen4_deviceEvents();
            Screen4_windowEvents();
            Screen4_elementsEvents();
        };

    // screen window events


    function Screen4_windowEvents() {

        $('#Screen4').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function Screen4_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Screen4_elementsExtraJS() {
        // screen (Screen4) extra code

    };

    // screen elements handler


    function Screen4_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Screen4_mobileheader [name="mobilebutton_11"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    $('[id="Screen4_panel_2"]').panel("open");

                }
            },
        }, '#Screen4_mobileheader [name="mobilebutton_11"]');

        $(document).off("click", '#Screen4_panel_2 [name="Crops"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('crops', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        }, '#Screen4_panel_2 [name="Crops"]');
        $(document).off("click", '#Screen4_panel_2 [name="Insurance"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Insurance', {
                        transition: 'pop',
                        reverse: false
                    });

                }
            },
        }, '#Screen4_panel_2 [name="Insurance"]');
        $(document).off("click", '#Screen4_panel_2 [name="Agrotips"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Agrotips', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        }, '#Screen4_panel_2 [name="Agrotips"]');

    };

    $(document).off("pagebeforeshow", "#Screen4").on("pagebeforeshow", "#Screen4", function(event, ui) {
        Apperyio.CurrentScreen = "Screen4";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Screen4_onLoad();
};

$(document).off("pagecreate", "#Screen4").on("pagecreate", "#Screen4", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Screen4_js();
});